import {useReducer} from "react"
import axios from "axios"

const SignIn = async (userId,userPw)=>{
    var check= await axios.post("http://localhost:4000/signin",{
        userId:userId,
        userPw:userPw
    })
    console.log(check)
  }
  
  const SingUp = () =>{
  
  }

const initialState={
    modal:{
      showSI:false,
      showSU:false,
    },
    signin:{
      userId:"",
      userPw:""
    },
    signup:{
        userId:"",
        userPw:"",
        userName:"",
        userEmail:"",
        userPhone:"",
        userJob:""
    }
  }
  const modalReducer=(state,action)=>{
    switch(action){
      case "openSI":{
        return{...state,showSI:true}
      }
      case "openSU":{
        return{...state,showSU:true}
      }
      case "hideSI":{
        return{...state,showSI:false}
      }
      case "hideSU":{
        return{...state,showSU:false}
      }
      default:{
        throw new Error("Error")
      }
    }
  }
  const signinReducer=async (state,action)=>{
        console.log(action.type)
        switch(action.type){
            case "setId":{
                return{...state,userId:action.userId}
            }
            case "setPw":{
                return{...state,userPw:action.userPw}
            }
            case "setData":{
                if(!(state.userId&&state.userPw)){
                    return false;
                }
                var a= await SignIn(state.userId,state.userPw)
                console.log(a)
            }
            default:{
                throw new Error("Error")
            }
    }
  }
  export default (type) => {
      switch(type){
          case "modal":{
              return useReducer(modalReducer,initialState.modal);
          }
          case "signin":{
            return useReducer(signinReducer,initialState.signin);
          }
          case "signup":{
              return useReducer(modalReducer,initialState.signup)
          }
      }
  }
