import {useReducer} from "react"

const SignIn = ()=>{
    // axios.post("http://localhost:4000/signin",{
  
    // })
    alert("Okay")
  }
  
  const SingUp = () =>{
  
  }

const initialState={
    modal:{
      showSI:false,
      showSU:false,
    },
    signin:{
      userId:"",
      userPw:""
    },
    signup:{
        userId:"",
        userPw:"",
        userName:"",
        userEmail:"",
        userPhone:"",
        userJob:""
    }
  }
  const modalReducer=(state,action)=>{
    switch(action){
      case "openSI":{
        return{...state,showSI:true}
      }
      case "openSU":{
        return{...state,showSU:true}
      }
      case "hideSI":{
        return{...state,showSI:false}
      }
      case "hideSU":{
        return{...state,showSU:false}
      }
      default:{
        throw new Error("Error")
      }
    }
  }
  const signinReducer=(state,action)=>{
        console.log(action.type)
        switch(action.type){
            case "setId":{
                return{...state,userId:action.userId}
            }
            case "setPw":{
                return{...state,userPw:action.userPw}
            }
            case "setData":{
                if(!(action.userId&&action.userPw)){
                    return false;
                }
                alert(state)
                var data = SignIn()
            }
            default:{
                throw new Error("Error")
            }
    }
  }
  export default (type) => {
      switch(type){
          case "modal":{
              return useReducer(modalReducer,initialState.modal);
          }
          case "signin":{
            return useReducer(signinReducer,initialState.signin);
          }
          case "signup":{
              return useReducer(modalReducer,initialState.signup)
          }
      }
  }
